import React from 'react';
import { createAssistant, createSmartappDebugger } from '@salutejs/client';
import './App.css';

const initializeAssistant = (getState) => {
  if (process.env.NODE_ENV === 'development') {
    return createSmartappDebugger({
      token: process.env.REACT_APP_TOKEN ?? '',
      initPhrase: `Запусти ${process.env.REACT_APP_SMARTAPP}`,
      getState,
      nativePanel: {
        defaultText: 'ччччччч',
        screenshotMode: false,
        tabIndex: -1,
      },
    });
  } else {
    return createAssistant({ getState });
  }
};

class App extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      notes: [{ id: Math.random().toString(36).substring(7), title: 'тест' }],
      currentQuestion: 1,
      totalQuestions: 21,
      answers: Array(21).fill(null) // массив для хранения ответов
    };

    this.assistant = initializeAssistant(() => this.getStateForAssistant());
    this.assistant.on('data', (event) => {
      if (event.type === 'character') {
        console.log(`assistant.on(data): character: "${event?.character?.id}"`);
      } else if (event.type === 'insets') {
        console.log(`assistant.on(data): insets`);
      } else {
        const { action } = event;
        this.dispatchAssistantAction(action);
      }
    });

    this.nextQuestion = this.nextQuestion.bind(this);
    this.prevQuestion = this.prevQuestion.bind(this);
    this.handleOptionChange = this.handleOptionChange.bind(this);
  }

  nextQuestion() {
    this.setState(prevState => ({
      currentQuestion: Math.min(prevState.currentQuestion + 1, prevState.totalQuestions)
    }));
  }

  prevQuestion() {
    this.setState(prevState => ({
      currentQuestion: Math.max(prevState.currentQuestion - 1, 1)
    }));
  }

  handleOptionChange(event) {
    const { value } = event.target;
    this.setState(prevState => {
      const answers = [...prevState.answers];
      answers[prevState.currentQuestion - 1] = value;
      return { answers };
    });
  }

  getStateForAssistant() {
    const state = {
      item_selector: {
        items: this.state.notes.map(({ id, title }, index) => ({
          number: index + 1,
          id,
          title,
        })),
        ignored_words: [
          'добавить', 'установить', 'запиши', 'поставь', 'закинь', 'напомнить',
          'удалить', 'удали',
          'выполни', 'выполнил', 'сделал'
        ],
      },
    };
    return state;
  }

  dispatchAssistantAction(action) {
    if (action) {
      switch (action.type) {
        case 'add_note':
          return this.add_note(action);

        case 'done_note':
          return this.done_note(action);

        case 'delete_note':
          return this.delete_note(action);

        default:
          throw new Error();
      }
    }
  }

  add_note(action) {
    this.setState({
      notes: [
        ...this.state.notes,
        {
          id: Math.random().toString(36).substring(7),
          title: action.note,
          completed: false,
        },
      ],
    });
  }

  done_note(action) {
    this.setState({
      notes: this.state.notes.map((note) =>
        note.id === action.id ? { ...note, completed: !note.completed } : note
      ),
    });
  }

  _send_action_value(action_id, value) {
    const data = {
      action: {
        action_id: action_id,
        parameters: {
          value: value,
        },
      },
    };
    const unsubscribe = this.assistant.sendData(data, (data) => {
      const { type, payload } = data;
      console.log('sendData onData:', type, payload);
      unsubscribe();
    });
  }

  play_done_note(id) {
    const completed = this.state.notes.find(({ id }) => id)?.completed;
    if (!completed) {
      const texts = ['Молодец!', 'Красавчик!', 'Супер!'];
      const idx = (Math.random() * texts.length) | 0;
      this._send_action_value('done', texts[idx]);
    }
  }

  delete_note(action) {
    this.setState({
      notes: this.state.notes.filter(({ id }) => id !== action.id),
    });
  }

  handleButtonClick(value) {
    console.log(`Button clicked: ${value}`);
  }

  render() {
    const questions = [
      [
        "Я не чувствую себя расстроенным, печальным.",
        "Я расстроен.",
        "Я все время расстроен и не могу от этого отключиться.",
        "Я настолько расстроен и несчастлив, что не могу это выдержать."
      ],
      [
        "Я не ощущаю себя тревожным, взволнованным.",
        "Я ощущаю себя тревожным.",
        "Я все время тревожен и не могу расслабиться.",
        "Я настолько тревожен и взволнован, что не могу это выдержать."
      ],
      // ... добавьте остальные 19 наборов вариантов ответов в аналогичном формате
    ];
    const divisions = Array.from({ length: this.state.totalQuestions }, (_, index) => index + 1);
    const currentAnswer = this.state.answers[this.state.currentQuestion - 1];

    return (
      <div className="container">
        <div className="question-info">
          {divisions.map((division) => (
            <div key={division} className="division-container">
              <div className={`question-number ${division <= this.state.currentQuestion ? 'active' : ''}`}>
                {division}
              </div>
              <div className={`division ${division <= this.state.currentQuestion ? 'active' : ''}`} />
            </div>
          ))}
        </div>

        <div className="button-container">
          <input 
            type="radio" 
            id="option1" 
            name="options" 
            value="option1" 
            className="radio-button"
            checked={currentAnswer === 'option1'}
            onChange={this.handleOptionChange} 
          />
          <label htmlFor="option1" className="label-button">Я не чувствую себя расстроенным, печальным.</label>

          <input 
            type="radio" 
            id="option2" 
            name="options" 
            value="option2" 
            className="radio-button"
            checked={currentAnswer === 'option2'}
            onChange={this.handleOptionChange} 
          />
          <label htmlFor="option2" className="label-button">Я расстроен.</label>

          <input 
            type="radio" 
            id="option3" 
            name="options" 
            value="option3" 
            className="radio-button"
            checked={currentAnswer === 'option3'}
            onChange={this.handleOptionChange} 
          />
          <label htmlFor="option3" className="label-button">Я все время расстроен и не могу от этого отключиться.</label>

          <input 
            type="radio" 
            id="option4" 
            name="options" 
            value="option4" 
            className="radio-button"
            checked={currentAnswer === 'option4'}
            onChange={this.handleOptionChange} 
          />
          <label htmlFor="option4" className="label-button">Я настолько расстроен и несчастлив, что не могу это выдержать.</label>

          <div className="navigation-button-container">
            <button className="prev-button" onClick={() => this.prevQuestion()}>Предыдущий вопрос</button>
            <button className="next-button" onClick={() => this.nextQuestion()}>Следующий вопрос</button>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
