import React from 'react';
import { createAssistant, createSmartappDebugger } from '@salutejs/client';
import './App.css';

const initializeAssistant = (getState) => {
  if (process.env.NODE_ENV === 'development') {
    return createSmartappDebugger({
      token: process.env.REACT_APP_TOKEN ?? '',
      initPhrase: `Запусти ${process.env.REACT_APP_SMARTAPP}`,
      getState,
      nativePanel: {
        defaultText: 'ччччччч',
        screenshotMode: false,
        tabIndex: -1,
      },
    });
  } else {
    return createAssistant({ getState });
  }
};

class App extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      notes: [{ id: Math.random().toString(36).substring(7), title: 'тест' }],
      currentQuestion: 1,
      totalQuestions: 21,
      answers: Array(21).fill(null), // массив для хранения ответов
    };

    this.assistant = initializeAssistant(() => this.getStateForAssistant());
    this.assistant.on('data', (event) => {
      if (event.type === 'character') {
        console.log(`assistant.on(data): character: "${event?.character?.id}"`);
      } else if (event.type === 'insets') {
        console.log(`assistant.on(data): insets`);
      } else {
        const { action } = event;
        this.dispatchAssistantAction(action);
      }
    });

    this.nextQuestion = this.nextQuestion.bind(this);
    this.prevQuestion = this.prevQuestion.bind(this);
    this.handleOptionChange = this.handleOptionChange.bind(this);
    this.assistant.addAction('start_test', () => {
      this.start_questions();
    });

  }



  nextQuestion() {
    this.setState(prevState => ({
      currentQuestion: Math.min(prevState.currentQuestion + 1, prevState.totalQuestions)
    }));
  }

  prevQuestion() {
    this.setState(prevState => ({
      currentQuestion: Math.max(prevState.currentQuestion - 1, 1)
    }));
  }

  handleOptionChange(event) {
    const { value } = event.target;
    this.setState(prevState => {
      const answers = [...prevState.answers];
      answers[prevState.currentQuestion - 1] = value;
      return { answers };
    });
  }

  getStateForAssistant() {
    const state = {
      item_selector: {
        items: this.state.notes.map(({ id, title }, index) => ({
          number: index + 1,
          id,
          title,
        })),
        ignored_words: [
          'добавить', 'установить', 'запиши', 'поставь', 'закинь', 'напомнить',
          'удалить', 'удали',
          'выполни', 'выполнил', 'сделал'
        ],
      },
    };
    return state;
  }



  dispatchAssistantAction(action) {
    if (action) {
      switch (action.type) {
        case 'start_questions':
          // Начинаем чтение вопросов
          this.start_questions();
          break;

        case 'answer_question':
          // Обрабатываем ответ пользователя
          this.answer_question(action);
          break;

        default:
          throw new Error();
      }
    }
  }


  start_questions() {
    // Устанавливаем текущий вопрос на первый
    this.setState({
      currentQuestion: 1,
      answers: Array(this.state.totalQuestions).fill(null)
    }, () => {
      // Читаем первый вопрос
      this.speakCurrentQuestion();
    });
  }

  answer_question(action) {
    // Получаем индекс текущего вопроса
    const questionIndex = this.state.currentQuestion - 1;
    // Копируем массив ответов
    const answers = [...this.state.answers];
    // Устанавливаем ответ пользователя
    answers[questionIndex] = `option${action.optionNumber}`;

    // Обновляем состояние приложения
    this.setState({ answers }, () => {
      // Переходим к следующему вопросу или завершаем тест
      if (this.state.currentQuestion < this.state.totalQuestions) {
        this.nextQuestion();
      } else {
        this.speak('Тест завершен. Спасибо за участие!');
      }
    });
  }


