import React from 'react';
import { createAssistant, createSmartappDebugger } from '@salutejs/client';
import './App.css';

const initializeAssistant = (getState) => {
  if (process.env.NODE_ENV === 'development') {
    return createSmartappDebugger({
      token: process.env.REACT_APP_TOKEN ?? '',
      initPhrase: `Запусти ${process.env.REACT_APP_SMARTAPP}`,
      getState,
      nativePanel: {
        defaultText: 'ччччччч',
        screenshotMode: false,
        tabIndex: -1,
      },
    });
  } else {
    return createAssistant({ getState });
  }
};

class App extends React.Component {
  constructor(props) {
    super(props);
    console.log('constructor');

    this.state = {
      notes: [{ id: Math.random().toString(36).substring(7), title: 'тест' }],
      currentQuestion: 1, // Добавлено: номер текущего вопроса
      totalQuestions: 21, // Добавлено: общее количество вопросов
    };

    this.assistant = initializeAssistant(() => this.getStateForAssistant());
    this.assistant.on('data', (event) => {
      console.log(`assistant.on(data)`, event);
      if (event.type === 'character') {
        console.log(`assistant.on(data): character: "${event?.character?.id}"`);
      } else if (event.type === 'insets') {
        console.log(`assistant.on(data): insets`);
      } else {
        const { action } = event;
        this.dispatchAssistantAction(action);
      }
    });

    this.nextQuestion = this.nextQuestion.bind(this);
    this.prevQuestion = this.prevQuestion.bind(this);
  }

  nextQuestion() {
    this.setState(prevState => ({
      currentQuestion: Math.min(prevState.currentQuestion + 1, prevState.totalQuestions)
    }));
  }

  prevQuestion() {
    this.setState(prevState => ({
      currentQuestion: Math.max(prevState.currentQuestion - 1, 1)
    }));
  }

  getStateForAssistant() {
    console.log('getStateForAssistant: this.state:', this.state);
    const state = {
      item_selector: {
        items: this.state.notes.map(({ id, title }, index) => ({
          number: index + 1,
          id,
          title,
        })),
        ignored_words: [
          'добавить', 'установить', 'запиши', 'поставь', 'закинь', 'напомнить', // addNote.sc
          'удалить', 'удали',  // deleteNote.sc
          'выполни', 'выполнил', 'сделал' // выполнил|сделал
        ],
      },
    };
    console.log('getStateForAssistant: state:', state);
    return state;
  }

  dispatchAssistantAction(action) {
    console.log('dispatchAssistantAction', action);
    if (action) {
      switch (action.type) {
        case 'add_note':
          return this.add_note(action);

        case 'done_note':
          return this.done_note(action);

        case 'delete_note':
          return this.delete_note(action);

        default:
          throw new Error();
      }
    }
  }

  add_note(action) {
    console.log('add_note', action);
    this.setState({
      notes: [
        ...this.state.notes,
        {
          id: Math.random().toString(36).substring(7),
          title: action.note,
          completed: false,
        },
      ],
    });
  }

  done_note(action) {
    console.log('done_note', action);
    this.setState({
      notes: this.state.notes.map((note) =>
        note.id === action.id ? { ...note, completed: !note.completed } : note
      ),
    });
  }

  _send_action_value(action_id, value) {
    const data = {
      action: {
        action_id: action_id,
        parameters: {
          value: value,
        },
      },
    };
    const unsubscribe = this.assistant.sendData(data, (data) => {
      const { type, payload } = data;
      console.log('sendData onData:', type, payload);
      unsubscribe();
    });
  }

  play_done_note(id) {
    const completed = this.state.notes.find(({ id }) => id)?.completed;
    if (!completed) {
      const texts = ['Молодец!', 'Красавчик!', 'Супер!'];
      const idx = (Math.random() * texts.length) | 0;
      this._send_action_value('done', texts[idx]);
    }
  }

  delete_note(action) {
    console.log('delete_note', action);
    this.setState({
      notes: this.state.notes.filter(({ id }) => id !== action.id),
    });
  }

  handleButtonClick(value) {
    console.log(`Button clicked: ${value}`);
  }

  render() {
    console.log('render');

    // Создаем массив для отображения полоски с делениями
    const divisions = Array.from({ length: this.state.totalQuestions }, (_, index) => index + 1);

    return (
      <div className="container">
        {/* Информационное табло с номером текущего вопроса */}
        <div className="question-info">
          {/* Мапим деления и размещаем номера вопросов над каждым делени
          {/* Мапим деления и размещаем номера вопросов над каждым делением */}
          {divisions.map((division) => (
            <div key={division} className="division-container">
              {/* Номер текущего вопроса */}
              <div className={`question-number ${division <= this.state.currentQuestion ? 'active' : ''}`}>
                {division}
              </div>
              {/* Полоска с делениями */}
              <div
                className={`division ${division <= this.state.currentQuestion ? 'active' : ''}`}
              />
            </div>
          ))}
        </div>

        {/* Контейнер для кнопок */}
        <div className="button-container">
          {/* Ваши кнопки */}

          <input type="radio" id="option1" name="options" value="option1" className="radio-button" />
          <label htmlFor="option1" className="label-button" onClick={() => this.handleButtonClick('option1')}>Option 1</label>

          <input type="radio" id="option2" name="options" value="option2" className="radio-button" />
          <label htmlFor="option2" className="label-button" onClick={() => this.handleButtonClick('option2')}>Option 2</label>

          <input type="radio" id="option3" name="options" value="option3" className="radio-button" />
          <label htmlFor="option3" className="label-button" onClick={() => this.handleButtonClick('option3')}>Option 3</label>

          <input type="radio" id="option4" name="options" value="option4" className="radio-button" />          <label htmlFor="option4" className="label-button" onClick={() => this.handleButtonClick('option4')}>Option 4</label>

          {/* Кнопка для перехода к предыдущему вопросу */}
          <button onClick={() => this.prevQuestion()}>Предыдущий вопрос</button>

          {/* Кнопка для перехода к следующему вопросу */}
          <button className="NextQuestion" onClick={() => this.nextQuestion()}>Следующий вопрос</button>
        </div>
      </div>
    );
  }
}

export default App;
